mapper.select:
SELECT COUNT(*)
FROM GAMECART
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.insert:
INSERT INTO GAMECART (USER_NAME, GAME_ID, GAME_TITLE, BACKGROUND_IMAGE, PRICE, SALE_PRICE)
VALUES (
#{userName}
, #{gameId}
, #{title}
, #{backgroundImage}
, #{price}
, #{salePrice}
)

mapper.delete:
DELETE FROM GAMECART
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.select:
SELECT COUNT(*)
FROM REVIEW
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.insert:
INSERT INTO REVIEW (USER_NAME, GAME_ID, RATING, CONTENT)
VALUES (
#{userName}
, #{gameId}
, #{rating}
, #{content}
)

mapper.update:
UPDATE REVIEW
SET RATING = #{rating},
CONTENT = #{content},
UPDATED_AT = CURRENT_TIMESTAMP
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.select:
SELECT * FROM REVIEW
WHERE game_id = #{gameId}
ORDER BY created_at DESC

SELECT COUNT(*)
FROM GAME_LIKE
WHERE USER_NAME = #{userName} AND GAME_ID = #{gameId}

mapper.insert:
INSERT INTO GAME_LIKE (USER_NAME, GAME_ID, GAME_TITLE, BACKGROUND_IMAGE, PRICE, SALE_PRICE)
VALUES (
#{userName}
, #{gameId}
, #{title}
, #{backgroundImage}
, #{price}
, #{salePrice}
)

mapper.delete:
DELETE FROM GAME_LIKE
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}
