mapper.select:
SELECT COUNT(*)
FROM REVIEW
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.insert:
INSERT INTO REVIEW (USER_NAME, GAME_ID, RATING, CONTENT)
VALUES (
#{userName}
, #{gameId}
, #{rating}
, #{content}
)

mapper.update:
UPDATE REVIEW
SET RATING = #{rating},
CONTENT = #{content},
UPDATED_AT = CURRENT_TIMESTAMP
WHERE USER_NAME = #{userName}
AND GAME_ID = #{gameId}

mapper.select:
SELECT * FROM REVIEW
WHERE game_id = #{gameId}
ORDER BY created_at DESC

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS cnt
FROM GAME_LIKE
WHERE USER_NAME = #{userName} AND GAME_ID = #{gameId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS cnt
FROM GAME_CART
WHERE USER_NAME = #{userName} AND GAME_ID = #{gameId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS cnt
FROM GAME_DISCOUNT
WHERE GAME_ID = #{gameId}

{ call GET_DISCOUNT_WISHLIST(
#{P_USERNAME, mode=IN, jdbcType=VARCHAR},
#{P_CURSOR, mode=OUT, jdbcType=CURSOR, resultMap=discountWishlistResultMap}
) }
