mapper.insert:
INSERT INTO GAMECART (USER_NAME, GAME_ID, GAME_TITLE, BACKGROUND_IMAGE, PRICE, SALE_PRICE)
VALUES (
#{userName}
, #{gameId}
, #{title}
, #{backgroundImage}
, #{price}
, #{salePrice}
)

INSERT INTO REVIEW (USER_NAME, GAME_ID, RATING, CONTENT)
VALUES (
#{userName}
, #{gameId}
, #{rating}
, #{content}
)

mapper.select:
SELECT * FROM REVIEW
WHERE game_id = #{gameId}
ORDER BY created_at DESC
