mapper.update:
UPDATE USER_AUTHORITY
SET ROLE_ID = #{roleId}
WHERE USER_ID = #{userId}

UPDATE USERS
SET IS_ACTIVE = CASE WHEN IS_ACTIVE = 1 THEN 0 ELSE 1 END
WHERE USER_ID = #{userId}

UPDATE USERS
SET
EMAIL = #{email},
PROFILE_IMG = #{profileImg}
WHERE USER_ID = #{userId}

mapper.select:
SELECT PASSWORD
FROM USERS
WHERE USERNAME = #{username}
